import * as React from "react"
import { motion, addPropertyControls, ControlType, Color } from "framer"
import { colorFromToken } from "./lib/colorFromToken"
import { indentTitle } from "./lib/indentTitle"
import { defaultEvents, fontControls } from "./lib/constants"

/*
 ** BUTTON
 */

export interface ButtonProps {
    text: string
    fontSize: number
    defaultBackground: string
    defaultTextColor: string
    hoverBackground: string
    hoverTextColor: string
    pressedBackground: any
    pressedTextColor: any
    disabledBackground: any
    disabledTextColor: any
    shadow: boolean
    variant: string
    onTap: any
    radius: number
    topLeft: number
    topRight: number
    bottomRight: number
    bottomLeft: number
    isMixed: boolean
    paddingPerSide: boolean
    padding: number
    paddingTop: number
    paddingRight: number
    paddingBottom: number
    paddingLeft: number
    colorTransition: any
    scaleTransition: any
    whileTapScale: number
    whileHoverScale: number
    disabled: boolean
    alignment: string
    onClick?: any
    onMouseEnter?: any
    onMouseLeave?: any
    onMouseDown?: any
    onMouseUp?: any
    font: boolean
    fontFamily?: string
    fontWeight?: number
}

export function Button({
    text,
    fontSize,
    defaultBackground,
    defaultTextColor,
    hoverBackground,
    hoverTextColor,
    pressedBackground,
    pressedTextColor,
    disabledBackground,
    disabledTextColor,
    shadow,
    variant,
    onClick,
    onMouseEnter,
    onMouseLeave,
    onMouseDown,
    onMouseUp,
    radius,
    topLeft,
    topRight,
    bottomRight,
    bottomLeft,
    isMixed,
    paddingPerSide,
    padding,
    paddingTop,
    paddingRight,
    paddingBottom,
    paddingLeft,
    whileHoverScale,
    whileTapScale,
    colorTransition,
    scaleTransition,
    alignment,
    disabled,
    fontFamily,
    fontWeight,
}: ButtonProps) {
    const borderRadius = isMixed ? `${topLeft}px ${topRight}px ${bottomRight}px ${bottomLeft}px` : `${radius}px`
    const paddingValue = paddingPerSide
        ? `${paddingTop}px ${paddingRight}px ${paddingBottom}px ${paddingLeft}px`
        : padding
    const variants = {
        default: {
            background: Color(colorFromToken(defaultBackground)).toValue(),
            color: Color(colorFromToken(defaultTextColor)).toValue(),
            scale: 1,
            boxShadow: shadow
                ? "0px 2px 6px rgba(0,0,0,0.1), 0px 4px 16px rgba(0,0,0,0.1)"
                : "0px 0px 0px rgba(0,0,0,0)",
        },
        hover: {
            background: Color(colorFromToken(hoverBackground)).toValue(),
            color: Color(colorFromToken(hoverTextColor)).toValue(),
            scale: whileHoverScale,
            boxShadow: shadow
                ? "0px 2px 6px rgba(0,0,0,0.1), 0px 4px 16px rgba(0,0,0,0.1)"
                : "0px 0px 0px rgba(0,0,0,0)",
        },
        pressed: {
            background: Color(colorFromToken(pressedBackground)).toValue(),
            color: Color(colorFromToken(pressedTextColor)).toValue(),
            scale: whileTapScale,
            shadow: shadow ? "0px 1px 4px rgba(0,0,0,0.2), 0px 2px 4px rgba(0,0,0,0.2)" : "0px 0px 0px rgba(0,0,0,0)",
        },
        disabled: {
            background: Color(colorFromToken(disabledBackground)).toValue(),
            color: Color(colorFromToken(disabledTextColor)).toValue(),
            scale: 1,
            boxShadow: shadow
                ? "0px 2px 6px rgba(0,0,0,0.1), 0px 4px 16px rgba(0,0,0,0.1)"
                : "0px 0px 0px rgba(0,0,0,0)",
        },
    }

    const fontStyle = {
        ...(fontFamily && { fontFamily }),
        fontWeight,
    }

    return (
        <motion.button
            type={"button"}
            transition={{ scale: scaleTransition, default: colorTransition }}
            style={{
                ...style,
                fontSize,
                borderRadius,
                padding: paddingValue,
                // @ts-ignore
                textAlign: alignment,
                ...fontStyle,
            }}
            initial={disabled ? "disabled" : variant.toLowerCase()}
            animate={disabled ? "disabled" : variant.toLowerCase()}
            variants={variants}
            disabled={variant === "Disabled" || disabled}
            whileHover="hover"
            whileTap="pressed"
            {...(!disabled && {
                onClick,
                onMouseEnter,
                onMouseLeave,
                onMouseDown,
                onMouseUp,
            })}
        >
            {text}
        </motion.button>
    )
}

Button.defaultProps = {
    height: 45,
    width: 135,
    text: "Get started!",
    variant: "Default",
    shadow: true,
    fontSize: 14,
    defaultBackground: "#0099FF",
    defaultTextColor: "#ffffff",
    hoverBackground: "#0077FF",
    hoverTextColor: "#ffffff",
    pressedBackground: "#0088FF",
    pressedTextColor: "#ffffff",
    disabledBackground: "#f3f3f3",
    disabledTextColor: "#AAAAAA",
    disabled: false,
    radius: 10,
    padding: 10,
    whileTapScale: 0.95,
    whileHoverScale: 1.1,
    alignment: "center",
    fontWeight: 600,
    scaleTransition: {
        type: "spring",
        duration: 0.3,
        delay: 0,
        stiffness: 600,
        damping: 30,
        mass: 1,
    },
}

const style: React.CSSProperties = {
    border: "none",
    width: "100%",
    position: "absolute",
    margin: 0,
    left: 0,
    right: 0,
    top: 0,
    bottom: 0,
    color: "white",

    outline: "none",
    resize: "none",
    WebkitTapHighlightColor: "rgba(0, 0, 0, 0)",
    letterSpacing: "-0.2px",
}

addPropertyControls(Button, {
    text: {
        title: "Text",
        type: ControlType.String,
        defaultValue: "Hello Framer!",
    },
    alignment: {
        title: "Text Align",
        type: ControlType.SegmentedEnum,
        optionTitles: ["Left", "Center", "Right"],
        options: ["left", "center", "right"],
    },
    shadow: {
        title: "Shadow",
        type: ControlType.Boolean,
        defaultValue: true,
        enabledTitle: "Show",
        disabledTitle: "Hide",
    },
    ...fontControls,
    disabled: {
        title: "Disabled",
        type: ControlType.Boolean,
        enabledTitle: "Yes",
        disabledTitle: "No",
    },
    variant: {
        title: "State",
        type: ControlType.Enum,
        defaultValue: "Default",
        options: ["Default", "Hover", "Pressed", "Disabled"],
    },
    defaultBackground: {
        title: indentTitle("Bg Color"),
        type: ControlType.Color,
        defaultValue: Button.defaultProps.defaultBackground,
        hidden: ({ variant }) => variant !== "Default",
    },
    defaultTextColor: {
        title: indentTitle("Text Color"),
        type: ControlType.Color,
        defaultValue: Button.defaultProps.defaultTextColor,
        hidden: ({ variant }) => variant !== "Default",
    },
    hoverBackground: {
        title: indentTitle("Bg Color"),
        type: ControlType.Color,
        defaultValue: Button.defaultProps.hoverBackground,
        hidden: ({ variant }) => variant !== "Hover",
    },
    hoverTextColor: {
        title: indentTitle("Text Color"),
        type: ControlType.Color,
        defaultValue: Button.defaultProps.hoverTextColor,
        hidden: ({ variant }) => variant !== "Hover",
    },
    pressedBackground: {
        title: indentTitle("Bg Color"),
        type: ControlType.Color,
        defaultValue: Button.defaultProps.pressedBackground,
        hidden: ({ variant }) => variant !== "Pressed",
    },
    pressedTextColor: {
        title: indentTitle("Text Color"),
        type: ControlType.Color,
        defaultValue: Button.defaultProps.pressedTextColor,
        hidden: ({ variant }) => variant !== "Pressed",
    },
    disabledBackground: {
        title: indentTitle("Bg Color"),
        type: ControlType.Color,
        defaultValue: Button.defaultProps.disabledBackground,
        hidden: ({ variant }) => variant !== "Disabled",
    },
    disabledTextColor: {
        title: indentTitle("Text Color"),
        type: ControlType.Color,
        defaultValue: Button.defaultProps.disabledTextColor,
        hidden: ({ variant }) => variant !== "Disabled",
    },
    padding: {
        title: "Padding",
        type: ControlType.FusedNumber,
        toggleKey: "paddingPerSide",
        toggleTitles: ["Padding", "Padding per side"],
        valueKeys: ["paddingTop", "paddingRight", "paddingBottom", "paddingLeft"],
        valueLabels: ["T", "R", "B", "L"],
        min: 0,
    },
    radius: {
        title: "Radius",
        type: ControlType.FusedNumber,
        defaultValue: Button.defaultProps.radius,
        toggleKey: "isMixed",
        toggleTitles: ["Radius", "Radius per corner"],
        valueKeys: ["topLeft", "topRight", "bottomRight", "bottomLeft"],
        valueLabels: ["TL", "TR", "BR", "BL"],
        min: 0,
    },
    fontSize: {
        type: ControlType.Number,
        title: "Font Size",
        min: 5,
        max: 25,
    },
    whileHoverScale: {
        title: "Hover Scale",
        type: ControlType.Number,
        min: 0.5,
        max: 1.5,
        step: 0.1,
    },
    whileTapScale: {
        title: "Tap Scale",
        type: ControlType.Number,
        min: 0.5,
        max: 1.5,
        step: 0.1,
    },
    scaleTransition: {
        title: "Scale Anim",
        type: ControlType.Transition,
    },
    colorTransition: {
        title: "Color Anim",
        type: ControlType.Transition,
    },
    ...defaultEvents,
})
